  const [countries, setCountries] = useState([]);
  const [inp, setInp] = useState("");
  const [option, setOption] = useState("")
  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((res) => res.json())
      .then((data) => {
        setCountries([...data]);
      });
  }, []);

